import React from 'react';
import { Text, View, TextInput } from 'react-native';
import Slider from '@react-native-community/slider';

const ChampAge = ({bonAge, ageSlider, ageTI , changeAge, changeAgeOk}) =>{
    const changeLesAges = (value) => {changeAge(value);
                                        ageSlider = value;
                                        ageTI = value
    }
    return  <View>
    <TextInput style = {StyleSheet.input} value = {ageTI} onChangeValue = {value => changeAge(value)}/>
    <Slider
        value= {ageSlider}
        onValueChange={changeLesAges}
        onSlidingComplete={(value) => changeAgeOk(value==bonAge)}
        step = {1}
        minimumValue = {20}
        maximumValue = {80}
        
    />
</View>
}

export default ChampAge;
