import React from 'react';
import { useState } from 'react';
import { Text } from 'react-native';
import {Slider} from 'react-native-sliders';

const SliderAge = ({bonAge, correct}) =>{
    [age, setAge] = useState(20);
    return  <View>
    <Slider
        value= {age}
        onValueChange={value => setAge(value)
                (value==bonAge) ? correct=true : correct = false}
        step = {1}
        minimumValue = {20}
        maximumValue = {80}
        thumbElement = {<Text>{this.value}</Text>}
    />
    <Text>La valeur rentr√©e est {correct ? "correct" : "incorrecte"}</Text>
</View>
}

export default SliderAge;
